,p_process_sequence=>10
,p_process_point=>'ON_DEMAND'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'RemoveAllEmployees'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_department_id departments.id%type;',
'    l_seq           number;',
'    l_has_staged_changes boolean := false;',
'    l_debug         boolean := false;',
'begin',
'    if l_debug then apex_debug.enable; end if;',
'    ',
'    l_department_id := apex_application.g_x01;',
'',
'    -- Ensure REMOVE collection exists',
'    if not apex_collection.collection_exists(''EMPLOYEES_REMOVE'') then',
'        apex_collection.create_collection(''EMPLOYEES_REMOVE'');',
'        apex_debug.message(''Created EMPLOYEES_REMOVE collection'');',
'    end if;',
'',
'    --Look for employees staged in EMPLOYEES_ADD for this l_department_id (department_id is stored in n002) and stage for removal.',
'    if apex_collection.collection_exists(''EMPLOYEES_ADD'') then',
'        for emp in (',
'            select seq_id, n001 as employee_id',
'            from apex_collections',
'            where collection_name = ''EMPLOYEES_ADD''',
'            and n002 = l_department_id',
'        ) loop',
'            -- Remove from ADD collection',
'            apex_collection.delete_member(',
'                p_collection_name => ''EMPLOYEES_ADD'',',
'                p_seq             => emp.seq_id',
'            );',
'            apex_debug.message(''Removed from ADD: '' || emp.employee_id);',
'',
'            -- Check if employee is already in REMOVE collection',
'            begin',
'                select seq_id into l_seq',
'                from apex_collections',
'                where collection_name = ''EMPLOYEES_REMOVE''',
'                and n001 = emp.employee_id;',
'                apex_debug.message(''Employee '' || emp.employee_id || '' already in REMOVE collection, skipping.'');',
'            exception',
'                when no_data_found then',
'                    -- Add to REMOVE collection if not already present',
'                    apex_collection.add_member(',
'                        p_collection_name => ''EMPLOYEES_REMOVE'',',
'                        p_n001            => emp.employee_id',
'                    );',
'                    apex_debug.message(''Added to REMOVE: '' || emp.employee_id);',
'            end;',
'        end loop;',
'    end if;',
'',
'    for emp in (',
'        select id',
'        from employees',
'        where department_id = l_department_id',
'    ) loop',
'        begin',
'            -- First check if employee exists in ADD collection',
'            if apex_collection.collection_exists(''EMPLOYEES_ADD'') then',
'                -- If an employee from this department is staged for assignment, it means they are being reassigned elsewhere.',
'                -- In this case, they should not be removed from the ADD collection or added to REMOVE.',
'                select seq_id into l_seq',
'                from apex_collections',
'                where collection_name = ''EMPLOYEES_ADD''',
'                    and n001 = emp.id;',
'                apex_debug.message(''Employee '' || emp.id || '' exists in ADD collection, skipping removal.'');',
'            else',
'                -- Check if employee is already in REMOVE collection',
'                begin',
'                    select seq_id into l_seq',
'                    from apex_collections',
'                    where collection_name = ''EMPLOYEES_REMOVE''',
'                    and n001 = emp.id;',
'                    apex_debug.message(''Employee '' || emp.id || '' already in REMOVE collection, skipping.'');',
'                exception',
'                    when no_data_found then',
'                        -- Add to REMOVE if not in REMOVE collection',
'                        apex_collection.add_member(',
'                            p_collection_name => ''EMPLOYEES_REMOVE'',',
'                            p_n001            => emp.id',
'                        );',
'                        apex_debug.message(''Added to REMOVE: '' || emp.id);',
'                end;',
'            end if;',
'        exception',
'            when no_data_found then',
'                -- Check if employee is already in REMOVE collection',
'                begin',
'                    select seq_id into l_seq',
'                    from apex_collections',
'                    where collection_name = ''EMPLOYEES_REMOVE''',
'                    and n001 = emp.id;',
'                    apex_debug.message(''Employee '' || emp.id || '' already in REMOVE collection, skipping.'');',
'                exception',
'                    when no_data_found then',
'                        -- Add to REMOVE if not in REMOVE collection',
'                        apex_collection.add_member(',
'                            p_collection_name => ''EMPLOYEES_REMOVE'',',
'                            p_n001            => emp.id',
'                        );',
'                        apex_debug.message(''Added to REMOVE: '' || emp.id);',
'                end;',
'        end;',
